INSERT INTO `web_product` VALUES ('107', null, 'Chăn lưới 4 mùa cho bé', '170000', '0', '170000', '1', '', '2', '<p><span style=\"background-color:rgb(255, 255, 255)\">Chăn lưới bốn m&ugrave;a l&agrave; loại chăn c&oacute; cấu tr&uacute;c c&aacute;c sợi cotton đan kiểu mắt lưới v&agrave;o với nhau. Chăn lưới ko chỉ th&iacute;ch hợp cho trẻ sơ sinh,m&agrave; c&ograve;n th&iacute;ch hợp cho người lớn đặc biệt l&agrave; người gi&agrave; v&agrave; c&aacute;c mẹ bầu.</span></p>\r\n\r\n<p>Chất liệu :100% Cotton</p>\r\n\r\n<p>Nhập khẩu từ&nbsp;Trung Quốc</p>\r\n\r\n<p>K&iacute;ch thước&nbsp;100 x 140 x 2cm</p>\r\n', '<h2>Chi tiết sản phẩm</h2>\r\n\r\n<p><strong>chăn lưới bốn m&ugrave;a cho b&eacute;</strong><span style=\"background-color:rgb(255, 255, 255); font-family:arial; font-size:12px\">: Chăn lưới bốn m&ugrave;a h&agrave;ng xuất Nga với chất liệu 100% cotton cho b&eacute; c&oacute; giấc ngủ th&ocirc;ng tho&aacute;ng kh&ocirc;ng mồ h&ocirc;i. H&agrave;ng chỉ c&oacute; tại Colorful Shop, gi&aacute; cực y&ecirc;u.</span></p>\r\n\r\n<ul>\r\n	<li>Chăn lưới bốn m&ugrave;a l&agrave; loại chăn c&oacute; cấu tr&uacute;c đặc biệt khi c&aacute;c sợi cotton được đan kiểu mắt lưới v&agrave;o với nhau.&nbsp;Chăn lưới ko chỉ th&iacute;ch hợp cho trẻ sơ sinh, trẻ nhỏ m&agrave; c&ograve;n th&iacute;ch hợp cho người lớn đặc biệt l&agrave; người gi&agrave; v&agrave; c&aacute;c mẹ bầu để c&oacute; cảm gi&aacute;c thoải m&aacute;i nhất.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Chăn lưới bốn mùa\" src=\"http://www.sacmauchobe.com/media/product/16457_chan_luoi2.jpg\" /></p>\r\n\r\n<h4 style=\"text-align:center\"><span style=\"color:rgb(51, 102, 255)\"><em><strong><span style=\"font-size:10pt\">Chăn lưới bốn m&ugrave;a m&agrave;u xanh dương.</span></strong></em></span></h4>\r\n\r\n<ul>\r\n	<li>C&aacute;c mắt lưới ko những gi&uacute;p tạo sự th&ocirc;ng tho&aacute;ng cho l&agrave;n da b&eacute; m&agrave; c&ograve;n l&agrave;m giảm nguy cơ đột tử khi ngủ cho c&aacute;c b&eacute; v&ocirc; t&igrave;nh &uacute;p chăn l&ecirc;n mặt.</li>\r\n	<li>Chăn c&oacute; t&aacute;c dụng k&eacute;p: M&ugrave;a lạnh chăn giữ ấm cho b&eacute; khi ngủ m&agrave; ko lo bị b&iacute;,bị hầm, kh&ocirc;ng tho&aacute;t được hồ m&ocirc;i. M&ugrave;a h&egrave;, từng cơn gi&oacute; sẽ len lỏi v&agrave;o từng mắt lưới nhỏ chạm v&agrave;o da thịt b&eacute;, khiến b&eacute; lu&ocirc;n c&oacute; cảm gi&aacute;c m&aacute;t mẻ thoải m&aacute;i. Thế mới gọi l&agrave;&nbsp;<strong>Chăn lưới bốn m&ugrave;a</strong>&nbsp;được chứ.</li>\r\n	<li>C&oacute; thể giữ ấm nhiều hơn nếu gấp đ&ocirc;i chăn lại khi đắp v&igrave; c&aacute;c mắt lưới xen kẽ, đan lại với nhau.&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Chăn lưới bốn mùa\" src=\"http://www.sacmauchobe.com/media/product/16457_chan_luoi4.jpg\" /></p>\r\n\r\n<h4 style=\"text-align:center\"><span style=\"color:rgb(51, 102, 255)\"><em><strong><span style=\"font-size:10pt\">Chăn lưới bốn m&ugrave;a m&agrave;u hồng.</span></strong></em></span></h4>\r\n\r\n<ul>\r\n	<li>C&aacute;c mắt lưới đan v&agrave;o nhau c&oacute; độ kh&iacute;t vừa phải để c&oacute; độ tho&aacute;ng kh&iacute; cho b&eacute; v&agrave; chắc chắn lại vừa tạo được độ mềm rủ chắc tay cho chăn.</li>\r\n	<li>Chăn c&oacute; độ bền cao, kh&ocirc;ng nh&atilde;o theo thời gian v&igrave; mắt lưới chắc chắn, kh&ocirc;ng phai m&agrave;u.</li>\r\n	<li>C&oacute; thể gấp lại l&agrave;m gối cho b&eacute; tho&aacute;ng đầu hoặc trải l&agrave;m thảm cho b&eacute; chơi.</li>\r\n	<li>Thậm ch&iacute;, chăn lưới bốn m&ugrave;a c&oacute; thể quấn ngo&agrave;i b&eacute; sơ sinh để tạo cảm gi&aacute;c th&ocirc;ng tho&aacute;ng thoải m&aacute;i, b&eacute; ngủ ngon giấc. C&aacute;c mẹ nhớ đặt một chiếc&nbsp;<a href=\"http://www.sacmauchobe.com/khan-xo-sua-golden-baby-3-lop/p16322.html\" style=\"text-decoration: none; color: rgb(28, 91, 141); max-width: 100%; font-weight: bold;\">khăn x&ocirc;</a>&nbsp;v&agrave;o cổ b&eacute; để tr&aacute;nh rớt sữa ra chăn nh&eacute;, m&ugrave;a n&agrave;y phải giặt chăn li&ecirc;n tục th&igrave; cực lắm.</li>\r\n</ul>\r\n\r\n<h4>C&aacute;ch bảo quản Chăn lưới bốn m&ugrave;a:&nbsp;</h4>\r\n\r\n<p>Giặt chăn ri&ecirc;ng với quần &aacute;o v&agrave; giặt bằng tay. Nếu giặt m&aacute;y, c&aacute;c mẹ n&ecirc;n cho v&agrave;o t&uacute;i giặt để đảm bảo độ bền. Vắt nhẹ v&agrave; phơi trong b&oacute;ng r&acirc;m.</p>\r\n\r\n<p>Kh&ocirc;ng d&ugrave;ng chất tẩy, chỉ l&agrave; ủ khi cần thiết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" bordercolor=\"#CCCCCC\" cellpadding=\"3\" cellspacing=\"0\" id=\"tb-product-spec\" style=\"background-color:rgb(255, 255, 255); border-collapse:collapse; border-color:rgb(238, 238, 238); color:rgb(0, 0, 0); font-family:arial; font-size:12px; line-height:21px; width:384px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-color:rgb(238, 238, 238)\">&nbsp;</td>\r\n			<td style=\"border-color:rgb(238, 238, 238)\">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '04-21-16-25-04-2016-1299862112390283394556322455009036866561048n.jpg', '04-21-16-25-04-2016-1299862112390283394556322455009036866561048n.jpg', 'a:1:{i:0;s:68:\"04-21-16-25-04-2016-1299862112390283394556322455009036866561048n.jpg\";}', '0', '181', 'Phụ kiện thời trang trẻ em', '0', '0', '1', '1', '6', 'Hàng xách tay', '2', '1', null, '22', '1461576076', '1462100779');
