INSERT INTO `web_product` VALUES ('113', null, 'THE FACESHOP NATURAL SUN SUPER PERFECT SUN CREAM', '160000', '0', '0', '1', '', '2', '<p>Xuất xứ:H&agrave;n Quốc</p>\r\n\r\n<p>D&ograve;ng chức năng:Dưỡng trắng da</p>\r\n\r\n<p>Loại da:Mọi loại da</p>\r\n\r\n<p>Hoạt t&iacute;nh:</p>\r\n\r\n<p>Dung t&iacute;ch/Khối lượng:50ml</p>\r\n', '<div style=\"clear:both;\">Th&ocirc;ng tin</div>\r\n\r\n<p><strong><em>Natural Sun Super Perfect Sun Cream</em></strong>&nbsp;với chiết xuất từ 600&micro;m&nbsp;hoa hướng dương c&oacute; t&aacute;c dụng ngăn chặn c&aacute;c tia UVA v&agrave; UVB ảnh hưởng đến l&agrave;n da. Bảo vệ l&agrave;n da tốt hơn v&agrave; giảm dầu nhờn, tạo cảm gi&aacute;c tho&aacute;i m&aacute;i, tự tin khi đi ra đường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" height=\"547\" src=\"http://myphamhq.vn/uploadwb/image/the%20faceshop/kem%20chong%20nang/the%20faceshop%20Natural%20Sun%20Super%20Perfect%20Sun%20Cream%202.jpg\" width=\"555\" /><img alt=\"\" height=\"228\" src=\"http://myphamhq.vn/uploadwb/image/the%20faceshop/kem%20chong%20nang/the%20faceshop%20Natural%20Sun%20Super%20Perfect%20Sun%20Cream%203.jpg\" width=\"555\" />&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong></p>\r\n\r\n<p>Đầu ti&ecirc;n mấy b&eacute; b&ocirc;i một lượng vừa đủ l&ecirc;n da mặt v&agrave; cổ sau đ&oacute; t&aacute;n theo chiều cấu tạo da</p>\r\n\r\n<p>Sử dụng trước v&agrave; sau khi ra ngo&agrave;i nắng trước 10 ph&uacute;t&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Khuyến c&aacute;o:&nbsp;</strong></p>\r\n\r\n<p>- Chỉ sử dụng trực tiếp tr&ecirc;n l&agrave;n da.</p>\r\n\r\n<p>- Tr&aacute;nh xa tầm tay của trẻ em.</p>\r\n\r\n<p>- Kh&ocirc;ng b&ocirc;i l&ecirc;n vết thương hở hoặc l&agrave;n da bị trầy xước. Tr&aacute;nh b&ocirc;i v&agrave;o mắt.</p>\r\n\r\n<div>&nbsp;</div>\r\n', '04-50-41-25-04-2016-the-faceshop-natural-sun-super-perfect-sun-cream-2.jpg', '04-50-41-25-04-2016-the-faceshop-natural-sun-super-perfect-sun-cream-2.jpg', 'a:1:{i:0;