INSERT INTO `web_product` VALUES ('114', null, 'Kem chống nắng Sundance của Đức', '265000', '0', '265000', '1', '', '2', '<p><span style=\"color:rgb(255, 0, 0)\"><strong>Kem chống nắng Sundance của Đức.</strong></span></p>\r\n\r\n<p>Bạn đang chuẩn bị tham gia một buổi du lịch/ d&atilde; ngoại ngo&agrave;i trời nhưng lại lo lắng về l&agrave;n da của m&igrave;nh sẽ bỏng r&aacute;t khi tiếp x&uacute;c l&acirc;u với &aacute;nh nắng.&nbsp;<em>Kem chống nắng Sundance của Đức</em>&nbsp;ch&iacute;nh l&agrave; giải ph&aacute;p tối ưu gi&uacute;p bạn ngăn chặn những tổn hại về da để bạn lu&ocirc;n tự tin v&agrave; rạng ngời ngay cả trong c&aacute;i nắng oi bức.</p>\r\n', '<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><strong>Xuất xứ v&agrave; c&ocirc;ng dụng của kem chống nắng Sundance của Đức.</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><em>Kem chống nắng Sundance&nbsp;</em>l&agrave; một trong những sản phẩm kem chống nắng được kh&aacute;ch h&agrave;ng ưa chuộng v&agrave; tin d&ugrave;ng trong suốt thời gian qua. Sundance l&agrave; sản phẩm chiến lược đến từ DM- c&ocirc;ng ty mỹ phẩm h&agrave;ng đầu&nbsp; của Đức. Đ&acirc;y l&agrave; thương hiệu quen thuộc của phụ nữ Đức cũng như h&agrave;ng triệu phụ nữ t&ecirc;n to&agrave;n Thế Giới. Với chỉ số chống nắng kh&aacute; cao, Sundance gi&uacute;p ngăn chặn những t&aacute;c hại từ tia UV đồng thời bảo vệ l&agrave;n da lu&ocirc;n khỏe mạnh. Đặc biệt sản phẩm n&agrave;y c&oacute; hiệu quả sử dụng k&eacute;o d&agrave;i sau nhiều giờ sử dụng, kh&ocirc;ng g&acirc;y cảm gi&aacute;c bết r&iacute;t tr&ecirc;n da. Th&ocirc;ng thường, da bạn sẽ trở n&ecirc;n xỉn m&agrave;u, kh&ocirc; r&aacute;p sau khi vui đ&ugrave;a dưới biển trong những ng&agrave;y h&egrave;, &nbsp;<em>kem chống nắng Sundance của Đức</em>&nbsp;sẽ tạo n&ecirc;n lớp m&agrave;n bảo vệ kh&ocirc;ng rửa tr&ocirc;i ngay cả khi bạn thỏa th&iacute;ch vui đ&ugrave;a h&agrave;ng giờ liền dưới nước.</span></p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><img alt=\"\" height=\"334\" src=\"http://mommybaby.vn/upload-files/4404436tai_xuong__1.jpg\" width=\"500\" /></span></p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><em>Kem chống nắng Sundance của Đức tạo n&ecirc;n lớp m&agrave;ng bảo vệ tối ưu cho da.</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\">&nbsp;</span></strong><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><strong>Ưu thế vượt trội của kem chống nắng Sundance của Đức.</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\">Với c&ocirc;ng nghệ ti&ecirc;n tiến nhất,&nbsp;<em>kem chống nắng Sundance của Đức</em>&nbsp;tạo n&ecirc;n một lớp m&agrave;ng lọc Meroxyl ngăn chặn tối ưu c&aacute;c tia tử ngoại g&acirc;y hại cho l&agrave;n da. Đồng thời việc sử dụng thường xuy&ecirc;n cũng gi&uacute;p giảm nguy cơ h&igrave;nh th&agrave;nh c&aacute;c đốm n&acirc;u, th&acirc;m n&aacute;m, kh&ocirc; r&aacute;p v&agrave; xỉn m&agrave;u da do t&aacute;c động trực tiếp của &aacute;nh nắng mặt trời. Ngo&agrave;i việc l&agrave;m giảm qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a da do t&aacute;c động m&ocirc;i trường, Sundance c&ograve;n l&agrave;m cải thiện sắc tố da, gi&uacute;p da đều m&agrave;u, rạng rỡ. Đặc biệt,&nbsp;<em>kem chống nắng Sundance của Đức</em>&nbsp;c&oacute; thể sử dụng cho cả mặt v&agrave; to&agrave;n th&acirc;n n&ecirc;n kh&aacute;ch h&agrave;ng ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m m&agrave; kh&ocirc;ng lo sợ k&iacute;ch ứng da.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><img alt=\"\" height=\"669\" src=\"http://mommybaby.vn/upload-files/KCNnguoilon.jpg\" width=\"500\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><em>Tạm biệt l&agrave;n da đen sạm, sỉn m&agrave;u với kem chống nắng Sundance của Đức.</em></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\"><strong>C&aacute;ch sử dụng, liều d&ugrave;ng v&agrave; một v&agrave;i lưu &yacute; về kem chống nắng Sundance của Đức.</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\">- Liều d&ugrave;ng: lấy một lượng vừa đủ&nbsp;<em>kem chống nắng Sundance của Đức</em>, thoa đều l&ecirc;n to&agrave;n bộ bề mặt da mặt v&agrave; to&agrave;n th&acirc;n( những vị tr&iacute; dễ chịu ảnh hưởng của &aacute;nh nắng mặt trời: cổ, vai, c&aacute;nh tay&hellip;). Lưu &yacute;: kh&ocirc;ng n&ecirc;n thoa một lớp&nbsp; d&agrave;y, m&agrave; chỉ một lớp mỏng vừa đủ.</span></p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\">- C&aacute;ch d&ugrave;ng:</span></p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\">+ N&ecirc;n thoa 30 ph&uacute;t trước khi ra nắng để kem được thẩm thấu v&agrave;o da v&agrave; ph&aacute;t huy t&aacute;c dụng.</span></p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\">+ Khi tiếp x&uacute;c qu&aacute; l&acirc;u dưới nắng, sau 3-4 giờ n&ecirc;n thoa kem lại một lần để đảm bảo hiệu quả chống nắng tối ưu.</span></p>\r\n\r\n<p><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:small\">-&nbsp;<em>Kem chống nắng Sundance của Đức</em>&nbsp;ho&agrave;n to&agrave;n kh&ocirc;ng g&acirc;y k&iacute;ch ứng da, kể cả da nhờn v&agrave; mụn.</span></p>\r\n\r\n<p><span styl