INSERT INTO `web_product` VALUES ('188', null, 'KEM NỀN CELLIO COLLAGEN BLEMISH BALM B.B HÀN QUỐC', '255000', '265000', '0', '1', '', '2', '<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">BB Collagen Blemish Balm&nbsp;</span><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">Th&agrave;nh phần: BB cream Collagen Cellio được chiết xuất từ 90% collagen tự nhi&ecirc;n.</span><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">Th&iacute;ch hợp mọi loại da c&oacute; 2 m&agrave;u 21 v&agrave; 23.</span><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">T&aacute;c dụng: BB cream Collagen Cellio c&oacute; 4 t&aacute;c dụng trắng da, chống tia tử ngoại UV, chống nhăn v&agrave; chống l&atilde;o h&oacute;a với độ che phủ ho&agrave;n hảo mang lại độ đ&agrave;n hồi v&agrave; l&agrave;n da mịn m&agrave;ng một c&aacute;ch tự nhi&ecirc;n nhất!</span><br />\r\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">Trọng lương 40gr&nbsp;</span></p>\r\n', '<h2 style=\"text-align:center\"><span style=\"font-size:20px\">KEM NỀN CELLIO COLLAGEN BLEMISH BALM B.B H&Agrave;N QUỐC</span></h2>\r\n\r\n<div class=\"description\" style=\"padding: 0px; margin: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: tahoma; font-size: 12px; line-height: 22px; text-align: justify; background: rgb(255, 255, 255);\">\r\n<p><span style=\"font-size:16px\">- Kem nền Collagen Blemish Balm BB l&agrave; một loại mỹ phẩm kh&ocirc;ng thể thiếu của c&aacute;c chị em.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Kem nền c&oacute; chứa SPF 36, PA + + với t&aacute;c dụng ưu việt chống tia UVA/UVB từ &aacute;nh nắng mặt trời.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Gi&uacute;p l&agrave;n da h&ocirc; hấp dễ d&agrave;ng cả ng&agrave;y, ngăn ngừa sự sản sinh c&aacute;c vết th&acirc;m, n&aacute;m, t&agrave;n nhang. &nbsp;</span></p>