INSERT INTO `web_product` VALUES ('221', null, 'Váy xòe đen trắng', '390000', '460000', '390000', '1', '', '2', '<p>V&aacute;y x&ograve;e đen trắng</p>\r\n\r\n<p>Sự kết hợp tinh tế của 2 m&agrave;u tương phản tạo n&ecirc;n n&eacute;t đầy quyến rũ, trẻ trung.</p>\r\n\r\n<p>Phần tr&ecirc;n đen với chất liệu cotton co gi&atilde;n 4 chiều. Phần dưới chất th&ocirc; d&agrave;y dặn co gi&atilde;n.&nbsp;</p>\r\n\r\n<p>Đi l&agrave;m, đi chơi đều được.</p>\r\n', '<p><img src=\"http://www.shopcuatui.com.vn/image.php?type_dir=product&amp;id=221&amp;width=700&amp;height=700&amp;image=http://www.shopcuatui.com.vn/uploads/product/221/01-05-26-30-04-2016-img0216.jpg\" />&nbsp;</p>\r\n\r\n<p>Co gi&atilde;n 4 chiều</p>\r\n\r\n<p><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=221&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/221/01-09-04-30-04-2016-img0217.jpg\" /></p>\r\n', '01-05-26-30-04-2016-img0216.jpg', '01-09-04-30-04-2016-img0217.jpg', 'a:2:{i:0;s:31:\"01-05-26-30-04-2016-img0216.jpg\";i:1;s:31:\"01-09-04-30-04-2016-img0217.jpg\";}', '0', '106', 'Đầm, chân váy', '0', '0', '1', '1', '15', 'Thời trang nữ', '2', '1', null, '22', '1461996326', '1462011271');
