INSERT INTO `web_product` VALUES ('325', null, 'Máy lọ nước WPU3205', '4250000', '5299000', '4250000', '1', '', '2', '<p><strong>M&aacute;y lọc </strong><strong>nước Newlife WPU-320</strong><strong>5</strong></p>\r\n\r\n<p>Xuất Xứ: H&agrave;n Quốc</p>\r\n\r\n<p>M&agrave;u sắc: Xanh.</p>\r\n\r\n<p>K&iacute;ch thước: D&agrave;i 320mm x Rộng 120mm x Cao 400mm</p>\r\n\r\n<p>Bảo h&agrave;nh: 36 th&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=325&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/325/01-17-09-11-05-2016-timthumb.jpg\" /></p>\r\n\r\n<p><strong>Đặc t&iacute;nh</strong>: M&aacute;y lọc nước Newlife dẫn nguồn trưc tiếp (kh&ocirc;ng nước thải) kh&ocirc;ng d&ugrave;ng điện, kh&ocirc;ng m&aacute;y bơm, kh&ocirc;ng b&igrave;nh chứa. C&ocirc;ng nghệ m&agrave;ng si&ecirc;u lọc UF</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Tiện &iacute;ch:&nbsp;</strong>Người ti&ecirc;u d&ugrave;ng c&oacute; thể sử dụng như nước ăn, uống, rửa rau.., m&agrave; kh&ocirc;ng phải mất tiền mua nước ăn, uống hằng ng&agrave;y v&agrave; c&aacute;c vật dụng để chứa nước.</p>\r\n\r\n<p>&nbsp;M&aacute;y lọc nước Newlife c&oacute; hệ thống bộ lọc gồm 5 l&otilde;i lọc:</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + L&otilde;i lọc số 1: Sendiment - l&agrave;m từ phorimer: thực hiện bước lọc 1&amp;2 ngăn chặn một số tạp chất th&ocirc; như: đất, c&aacute;t, b&ugrave;n, rong r&ecirc;u, rỉ s&eacute;t c&aacute;c chất rắn kh&ocirc;ng h&ograve;a tan. Thời gian thay l&otilde;i lọc từ 4-6 th&aacute;ng.</p>\r\n\r\n<p style=\"margin-left:36pt\">+ L&otilde;i lọc số 2: Pre carbon &ndash; được l&agrave;m từ than hoạt t&iacute;nh, thực hiện c&aacute;c bước lọc 3&amp;4 ngăn chặn c&aacute;c tạp chất được tiết ra từ dầu mỏ, clo, kim loại nặng, thuốc trừ s&acirc;u, hấp thụ tất cả c&aacute;c loại m&ugrave;i vị kh&ocirc;ng tốt được h&ograve;a tan trong nước. Thời gian thay l&otilde;i lọc từ 8-10 th&aacute;ng.</p>\r\n\r\n<p style=\"margin-left:36pt\">+ L&otilde;i lọc số 3: M.UF membrane filter &ndash; M&agrave;ng si&ecirc;u lọc c&ocirc;ng nghệ UF, ngăn chặn triệt để tới 99,9% tất cả c&aacute;c loại virut, vi khuẩn trong nước. Khử to&agrave;n bộ chất độc hại cho sức khỏe con người. M&agrave;ng si&ecirc;u lọc UF chỉ cho nước nguy&ecirc;n kho&aacute;ng c&oacute; lợi cho cơ thể. Thời gian thay l&otilde;i lọc từ 14-18 th&aacute;ng.</p>\r\n\r\n<p style=\"margin-left:36pt\">+ L&otilde;i lọc số 4: Post carbon &ndash; Được l&agrave;m từ than hoạt t&iacute;nh cao cấp, l&agrave; bước lọc sau m&agrave;ng si&ecirc;u lọc UF, tiếp tục hấp thụ v&agrave; lọc tất cả c&aacute;c tạp chất, hỗn hợp, &aacute;c loại m&ugrave;i d&ugrave; l&agrave; nhỏ nhất để mang lại nguồn nước trong l&agrave;nh m&agrave; vẫn giữ nguy&ecirc;n c&aacute;c nguồn kho&aacute;ng chất tốt nhất cho cơ thể. Thời gian thay l&otilde;i lọc từ 12-14 th&aacute;ng.</p>\r\n\r\n<p style=\"margin-left:36pt\">+ L&otilde;i lọc số 5: TCR &ndash; Được l&agrave;m từ than hoạt t&iacute;nh cao cấp, l&otilde;i lọc phụ nhằm thanh lọc nguồn nước nguy&ecirc;n kho&aacute;ng ho&agrave;n to&agrave;n. Nguồn nước được lọc th&ecirc;m một lần nữa v&agrave; khử c&aacute;c m&ugrave;i do hệ thống g&acirc;y ra. Thời gian thay l&otilde;i lọc từ 12-16 th&aacute;ng.</p>\r\n', '01-17-09-11-05-2016-timthumb.jpg', '01-17-09-11-05-2016-timthumb.jpg', 'a:1:{i:0;s:32:\"01-17-09-11-05-2016-timthumb.jpg\";}', '0', '27', 'Đồ điện gia dụng', '0', '0', '1', '1', '25', 'MÁY LỌC NƯỚC NEWLIFE', '2', '1', null, '29', '1462947429', '1462948768');
