INSERT INTO `web_product` VALUES ('588', null, 'Dung dịch vệ sinh phụ nữ Crevil', '95000', '0', '0', '1', '', '-1', '<p>Dung dịch vệ sinh phụ nữ CREVIL&nbsp;gi&uacute;p vệ sinh diệt khuẩn, kh&aacute;ng vi&ecirc;m, hỗ trợ điều trị c&aacute;c bệnh vi&ecirc;m nhiễm phụ khoa, bảo vệ m&ocirc;i trường PH tự nhi&ecirc;n, giữ da mềm mại v&agrave; lưu m&ugrave;i hương nhẹ nh&agrave;ng dễ chịu. Đạt&nbsp;ti&ecirc;u chuẩn y tế Ch&acirc;u &Acirc;u.</p>\r\n', '<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Vệ sinh phụ nữ cao cấp Crevil intim 100ml được lưu h&agrave;nh theo ti&ecirc;u chuẩn y tế Ch&acirc;u &Acirc;u. - Kh&ocirc;ng chứa x&agrave; ph&ograve;ng - Bảo vệ m&ocirc;i trường PH tự nhi&ecirc;n ở v&ugrave;ng da nhạy cảm - Chiết xuất từ Hoa c&uacute;c v&agrave; X&ocirc; thơm bảo vệ da.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Sản phẩm được nhập khẩu trực tiếp từ CHLB Đức v&agrave; được cấp ph&eacute;p lưu h&agrave;nh bởi Cục Quản l&yacute; dược phẩm - mỹ Phẩm, Bộ Y Tế.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Th&agrave;nh phần chiết xuất từ hoa c&uacute;c La M&atilde;( Chamomilla Recutita), Chiết xuất từ l&aacute; c&acirc;y X&ocirc; Thơm(Salvia Officinalis leaf), Protein được thủy ph&acirc;n từ sữa c&ugrave;ng c&aacute;c th&agrave;nh phần kh&aacute;c, c&ocirc;ng thức v&agrave; ti&ecirc;u chuẩn y tế Ch&acirc;u &Acirc;u, dung dịch vệ sinh phụ nữ CREVIL INTIM WASCHLOTION gi&uacute;p vệ sinh diệt khuẩn, kh&aacute;ng vi&ecirc;m, hỗ trợ điều trị c&aacute;c bệnh vi&ecirc;m nhiễm phụ khoa, bảo vệ m&ocirc;i trường PH tự nhi&ecirc;n, giữ da mềm mại v&agrave; lưu m&ugrave;i hương nhẹ nh&agrave;ng dễ chịu.</span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\"><img src=\"http://shopcuatui.com.vn/image.php?type_d