INSERT INTO `web_product` VALUES ('595', null, 'Hàu tam hiệp tăng cường sinh lý nam', '260000', '265000', '0', '1', '', '-1', '<p><strong>H&agrave;u Tam Hiệp&nbsp;</strong><span style=\"color:rgb(68, 68, 68)\">được chiết xuất ho&agrave;n to&agrave;n từ thịt h&agrave;u tươi đ&aacute;nh bắt ở bờ biển Việt Nam.&nbsp;</span><strong>H&agrave;u Tam Hiệp&nbsp;</strong><span style=\"color:rgb(68, 68, 68)\">chứa h&agrave;m lượng</span><strong>&nbsp;Kẽm</strong><span style=\"color:rgb(68, 68, 68)\">&nbsp;rất cao ho&agrave;n to&agrave;n tự nhi&ecirc;n v&agrave; được bổ sung&nbsp;</span><strong>vitamin E</strong><span style=\"color:rgb(68, 68, 68)\">&nbsp;gi&uacute;p tinh tr&ugrave;ng ph&aacute;t triển tốt hơn , n&acirc;ng cao kết quả điều trị v&ocirc; sinh.</span><strong>H&agrave;u Tam Hiệp&nbsp;</strong><span style=\"color:rgb(68, 68, 68)\">&nbsp;kh&ocirc;ng chỉ hỗ trợ, tăng cường một c&aacute;ch tự nhi&ecirc;n , t&aacute;i tạo sinh lực cho nam giới,</span><span style=\"color:rgb(68, 68, 68)\">&nbsp;l&agrave; giải ph&aacute;p hiệu quả gi&uacute;p tăng số lượng v&agrave; cải thiện chất lượng tinh tr&ugrave;ng, giảm tỷ lệ v&ocirc; sinh, duy tr&igrave; phong độ cho nam giới.</span></p>\r\n', '<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Thực phẩm chức năng <strong>H&agrave;u Tam Hiệp</strong> c&oacute; th&agrave;nh phần&nbsp;chiết xuất H&agrave;u Biển</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Gi&uacute;p tăng tiết testosterol nội sinh một c&aacute;ch tự nhi&ecirc;n, tăng cường chức năng sinh l&yacute; nam</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Gi&uacute;p l&agrave;m chậm qu&aacute; tr&igrave;nh m&atilde;n dục nam</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\"><strong>Th&agrave;nh phần:</strong></span></span></p>\r\n\r\n<u