INSERT INTO `web_product` VALUES ('596', null, 'Máy xay cầm tay Braun MQ5030 Special Sauce', '1899000', '2100000', '0', '1', '', '3', '<p><strong><u>TH&Ocirc;NG SỐ KỸ THUẬT</u></strong></p>\r\n\r\n<p>Model: MQ5030 Sauce Special</p>\r\n\r\n<p>Thương hiệu: Đức</p>\r\n\r\n<p>Sản xuất: Ba Lan</p>\r\n\r\n<p>M&agrave;u sắc: trắng, x&aacute;m</p>\r\n\r\n<p>C&ocirc;ng suất: 750W</p>\r\n\r\n<p>Phụ kiện: Cối xay 500ml chuy&ecirc;n dụng xay thịt v&agrave; cối 350ml chuy&ecirc;n dụng xay đồ kh&ocirc;.</p>\r\n\r\n<p>&nbsp;Ly đong 600ml c&oacute; ph&acirc;n chia mức đong r&otilde; r&agrave;ng.</p>\r\n\r\n<p>T&iacute;nh năng: 21 tốc độ ph&ugrave; hợp cho từng loại thực phẩm</p>\r\n\r\n<p>Chất liệu t&ocirc; xay: nhựa SAN (kh&ocirc;ng c&oacute; BPA)</p>\r\n\r\n<p>Chất liệu dao v&agrave; trục xay: th&eacute;p kh&ocirc;ng gỉ cao cấp</p>\r\n\r\n<p>Số v&ograve;ng quay: 12500 v&ograve;ng/ph&uacute;t</p>\r\n\r\n<p>D&ugrave;ng được cho m&aacute;y rửa ch&eacute;n</p>\r\n\r\n<p>Thời gian bảo h&agrave;nh: 24 th&aacute;ng</p>\r\n\r\n<p>K&iacute;ch thước: 181 x 289 x 405mm</p>\r\n\r\n<p>Trọng lượng: 2kg</p>\r\n', '<p><strong><u>ĐẶC ĐIỂM NỔI BẬT</u></strong></p>\r\n\r\n<p><strong>M&aacute;y xay cầm tay số 1 thế giới</strong></p>\r\n\r\n<p>Braun l&agrave; thương hiệu số 1 thế giới về doanh số m&aacute;y xay cầm tay, theo kết quả khảo s&aacute;t của viện nghi&ecirc;n cứu độc lập thực hiện từ 2006 -2014.</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>C&ocirc;ng nghệ Đức, sản xuất tại ch&acirc;u &Acirc;u</strong></p>\r\n\r\n<p>Sản phẩm <strong>Braun</strong> được ph&aacute;t triển dựa tr&ecirc;n ti&ecirc;u chuẩn chất lượng cao của Đức. Mỗi thiết bị đều được thử nghiệm kỹ lưỡng từ giai đoạn ph&aacute;t triển đến sản xuất sản phẩm.</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>Đi k&egrave;m 2 cối gi&uacute;p xay ri&ecirc;ng đồ ch&iacute;n v&agrave; sống</strong></p>\r\n\r\n<p>M&aacute;y xay Braun MQ5030 với thiết kế ưu việt ph&ugrave; hợp nhất với việc chế biến thức ăn cho em b&eacute;. Đi k&egrave;m 2 cối 500ml v&agrave; 350ml gi&uacute;p xay ri&ecirc;ng đồ sống v&agrave; ch&iacute;n, đảm bảo an to&agrave;n vệ sinh cho trẻ</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>21 tốc độ kh&aacute;c nhau</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ mới với 21 tốc độ gi&uacute;p bạn dễ d&agrave;ng thiết lập tốc độ ph&ugrave; hợp nhất để đạt được kết quả xay h&agrave;i l&ograve;ng nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>C&ocirc;ng suất 750W cực mạnh</strong></p>\r\n\r\n<p>C&ocirc;ng suất mạnh đi k&egrave;m n&uacute;t gia tốc Turbo gi&uacute;p bạn c&oacute; thể xay cắt hầu hết c&aacute;c loại thực phẩm trong thời gian nhanh nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>C&ocirc;ng nghệ đầu xay h&igrave;nh chu&ocirc;ng xoắn độc quyền</strong></p>\r\n\r\n<p>Cấu tạo đầu xay h&igrave;nh chu&ocirc;ng xoắn đặc biệt v&agrave; lưỡi dao được l&agrave;m từ chất liệu inox si&ecirc;u cứng n&ecirc;n thực phẩm được h&uacute;t v&agrave;o lưỡi xay do đ&oacute; xay rất nhanh, mịn v&agrave; kh&ocirc;ng bị văng ra ngo&agrave;i</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>Động cơ sản xuất tại Đức</strong><br />\r\nĐộng cơ c&aacute;c m&aacute;y xay cầm tay <strong>Braun</strong> được sản xuất tại Đức với độ ch&iacute;nh x&aacute;c cao để vận h&agrave;nh &iacute;t &nbsp;ồn, &iacute;t rung v&agrave; cho tuổi thọ l&acirc;u d&agrave;i</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>Bảo h&agrave;nh ch&iacute;nh h&atilde;ng 24 th&aacute;ng </strong><br />\r\nC&aacute;c sản phẩm <strong>Braun</strong> ch&iacute;nh h&atilde;ng c&oacute; tem chống h&agrave;ng giả, được ph&acirc;n phối bởi c&ocirc;ng ty Đối T&aacute;c Nh&atilde;n Hiệu sẽ được bảo h&agrave;nh 24 th&aacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>LƯU &Yacute; KHI SỬ DỤNG:</strong></p>\r\n\r\n<p>KH&Ocirc;NG xay li&ecirc;n tục QU&Aacute; 60 GI&Acirc;Y.</p>\r\n\r\n<p>Để m&aacute;y nghỉ 3 ph&uacute;t trước khi xay trở lại.</p>\r\n\r\n<p>Đầu c&acirc;y xay sử dụng được từ -20<sup>o</sup>C đến +80<sup>o</sup>C.</p>\r\n\r\n<p>Thức ăn phải được cắt nhỏ trước khi cho v&agrave;o cối.</p>\r\n\r\n<p>Chỉ cối xay 500ml v&agrave; 1.25l mới xay được thịt.</p>\r\n\r\n<p>Cối 350ml kh&ocirc;ng xay được thịt.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng bảo h&agrave;nh nếu qu&yacute; kh&aacute;ch sử dụng&nbsp; kh&ocirc;ng đ&uacute;ng như tr&ecirc;n</p>\r\n\r\n<p><strong><u>TH&Ocirc;NG TIN THƯƠNG HIỆU</u></strong></p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n\r\n<p><strong>Braun</strong> l&agrave; thương hiệu Đức h&agrave;ng đầu thế giới với lịch sử hơn 90 năm ph&aacute;t triển. M&aacute;y xay cầm tay <strong>Braun</strong> được sản xuất dựa tr&ecirc;n những ti&ecirc;u chuẩn chất lượng cao cấp nhất của Đức v&agrave; ch&acirc;u &Acirc;u. Với phương ch&acirc;m &ldquo;Thiết kế tạo n&ecirc;n sự kh&aacute;c biệt&rdquo;, <strong>Braun</strong> đ&atilde; cho ra đời những d&ograve;ng sản phẩm cao cấp với thiết kế đơn giản, h&agrave;i h&ograve;a, sang trọng nhưng vẫn đảm bảo t&iacute;nh năng ưu việt. C&aacute;c thiết kế của Braun được khẳng định với hơn 100 giải thưởng quốc tế danh gi&aacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"data:image/png;base64,\" />&nbsp;&nbsp;&nbsp;&nbsp; <img alt=\"\" src=\"data:image/png;base64,\" />&nbsp;&nbsp;&nbsp;&nbsp; <img alt=\"\" src=\"data:image/png;base64,\" />&nbsp;&nbsp;&nbsp;&nbsp; <img alt=\"\" src=\"data:image/png;base64,\" />&nbsp;&nbsp;&nbsp;&nbsp; <img alt=\"\" src=\"data:image/png;base64,\" /></p>\r\n', '10-15-40-07-06-2016-vario-special-white.jpg', '10-15-40-07-06-2016-vario-special-white.jpg', 'a:1:{i:0;s:43:\"10-15-40-07-06-2016-vario-special-white.jpg\";}', '1', '27', 'Đồ điện gia dụng', '0', '0', '1', '1', '59', 'Phan Lê', '1', '1', null, '29', '1465269262', '1465269358');
INSERT INTO `web_product` VALUES ('597', null, 'Eu thymo tăng sức đề kháng, giúp trẻ ăn ngon', '190000', '195000', '0', '1', '', '-1', '<p>Eu thymo chứa Thymomodulin gi&uacute;p&nbsp;tăng cường sức đề kh&aacute;ng, gi&uacute;p trẻ ăn ngon hấp thu tốt dưỡng chất, d&ugrave;ng tốt cho c&aacute;c trường hợp trẻ biếng ăn, chậm lớn, c&ograve;i xương, suy dinh dưỡng, hay quấy kh&oacute;c, thiếu m&aacute;u, sức đề kh&aacute;ng k&eacute;m,...</p>\r\n', '<p><strong>EU Thymo</strong> chứa <strong>Thymomodulin </strong>l&agrave;m tăng số lượng bạch cầu, đặc biệt l&agrave;m tăng đ&aacute;ng kể Lympho &ndash; T- tế b&agrave;o tham gia v&agrave;o chức năng miễn dịch của cơ thể.</p>\r\n\r\n<p>Với th&agrave;nh phần Thymomodulin, EU Thymo c&oacute; khả năng k&iacute;ch th&iacute;ch tủy xương sinh kh&aacute;ng thể, th&uacute;c đẩy th&agrave;nh lập phức hợp miễn dịch gi&uacute;p cơ thể <strong>chống lại c&aacute;c t&aacute;c nh&acirc;n g&acirc;y bệnh </strong>(đặc biệt l&agrave; c&aacute;c bệnh : vi&ecirc;m đường h&ocirc; hấp, vi&ecirc;m gan, vi&ecirc;m khớp dạng thấp) đồng thời l&agrave;m giảm c&aacute;c kh&aacute;ng thể g&acirc;y dị ứng &ndash;IGE ở người vi&ecirc;m mũi dị ứng, hen, vi&ecirc;m da dị ứng v&agrave; dị ứng thức ăn ở trẻ nhỏ.</p>\r\n\r\n<p>EU Thymo được chỉ định cho bệnh nh&acirc;n ung thư để l&agrave;m giảm c&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn của h&oacute;a xạ trị, do n&oacute; c&oacute; t&aacute;c dụng ph&ograve;ng v&agrave; l&agrave;m giảm bớt sự ph&aacute; hủy tế b&agrave;o tủy xương.</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=597&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/597/03-26-22-07-06-2016-eu-thymo.jpg\" /></p>\r\n\r\n<p>Sản phẩm mới, được bổ sung Pluriamin được chiết xuất từ nhộng tằm c&oacute; t&aacute;c dụng cung cấp c&aacute;c acidamin&nbsp;thiết yếu, l&agrave; nguy&ecirc;n liệu để tổng hợp Protein tạo ra năng lượng duy tr&igrave; sự sống.</p>\r\n\r\n<p>Sự phối hợp Thymomodulin, Pluriamin, L-lysin, kẽm v&agrave; vitamin nh&oacute;m B trong EU Thymo l&agrave; một giải ph&aacute;p mới gi&uacute;p tăng cường chức năng miễn dịch cho trẻ một c&aacute;ch ho&agrave;n thiện nhất. Sản phẩm hỗ trợ hệ thống miễn dịch, đồng thời k&iacute;ch th&iacute;ch trẻ ăn ngon, tăng hấp thu c&aacute;c chất dinh dưỡng, hai t&aacute;c dụng n&agrave;y hiệp đồng với nhau tạo ra hiệu quả tối ưu trong việc gi&uacute;p trẻ ph&ograve;ng ngừa bệnh tật v&agrave; ph&aacute;t triển khỏe mạnh. Đồng thời ưu việt gi&uacute;p phục hồi sức khỏe cho bệnh nh&acirc;n đang v&agrave; sau điều trị bệnh.</p>\r\n\r\n<p>&beta; &ndash; Glucan l&agrave;m giảm c&aacute;c triệu chứng của bệnh vi&ecirc;m mũi như : ngạt mũi, sổ mũi, đau họng, ho... . , l&agrave;m giảm sự oxi h&oacute;a tế b&agrave;o trong qu&aacute; tr&igrave;nh tiến triển bệnh vi&ecirc;m khớp, l&agrave;m tăng nhu động ruột, cải thiện c&aacute;c vấn đề đường ti&ecirc;u h&oacute;a, tăng cường miễn dịch tự nhi&ecirc;n, ngăn chặn c&aacute;c mầm bệnh x&acirc;m nhập từ b&ecirc;n ngo&agrave;i v&agrave;o cơ thể.</p>\r\n\r\n<p><strong>Th&agrave;nh phần</strong>: Trong mỗi g&oacute;i 3g</p>\r\n\r\n<p>&beta; &ndash; Glucan.......................40mg</p>\r\n\r\n<p>Thymomodulin ..............40mg</p>\r\n\r\n<p>Kẽm................................2mg</p>\r\n\r\n<p>L- Lysin..........................100mg</p>\r\n\r\n<p>Taurine............................50mg</p>\r\n\r\n<p>Pluriamin.........................80mg</p>\r\n\r\n<p>Vitamin B1......................0.45mg</p>\r\n\r\n<p>Vitamin B2......................0.4mg</p>\r\n\r\n<p>Vitamin B6......................0.4mg</p>\r\n\r\n<p>Cao men bia.....................50mg</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>-Bồi bổ cơ thể , gi&uacute;p cơ thể ph&aacute;t triển khỏe mạnh.</p>\r\n\r\n<p>Gi&uacute;p hấp thu v&agrave; ti&ecirc;u h&oacute;a tốt, được d&ugrave;ng hỗ trợ c&aacute;c trường hợp trẻ chậm lớn, c&ograve;i xương, suy dinh dưỡng, trẻ ch&aacute;n ăn, hay quấy kh&oacute;c, kh&oacute; ngủ, gầy c&ograve;m, thiếu m&aacute;u.</p>\r\n\r\n<p>Hỗ trợ tăng cường đề kh&aacute;ng v&agrave; khả năng miễn dịch của cơ thể, cũng như c&aacute;c trường hợp đang v&agrave; sau điều trị.</p>\r\n\r\n<p><strong>Đối tượng sử dụng</strong>&nbsp;: D&ugrave;ng tăng sức đề kh&aacute;ng trong c&aacute;c trường hợp:</p>\r\n\r\n<p>-Trẻ k&eacute;m hấp thu, biếng ăn, chậm ph&aacute;t triển.</p>\r\n\r\n<p>- C&aacute;c trường hợp thiếu kẽm.</p>\r\n\r\n<p>- Nam, nữ thiếu ni&ecirc;n từ 0-18 tuổi.</p>\r\n\r\n<p>- Người trưởng th&agrave;nh cơ thể suy yếu.</p>\r\n\r\n<p>- Phụ nữ c&oacute; thai v&agrave; cho con b&uacute;.</p>\r\n\r\n<p>- Đặc biệt bệnh nh&acirc;n dang điều trị bệnh v&agrave; trong giai đoạn phục hồi.</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng :</strong></p>\r\n\r\n<p>- Trẻ từ 07 th&aacute;ng &ndash; 3 tuổi : &frac12; g&oacute;i/lần x 2 lần/ng&agrave;y</p>\r\n\r\n<p>- Trẻ từ 4- 6 tuổi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : 1 g&oacute;i/lần x 2 lần/ng&agrave;y.</p>\r\n\r\n<p>- Trẻ từ 7 tuổi trở l&ecirc;n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : 1 g&oacute;i/lần x 3 lần/ng&agrave;y</p>\r\n\r\n<p>- Người trưởng th&agrave;nh&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : 1 g&oacute;i/lần x 4 lần/ng&agrave;y</p>\r\n\r\n<p>C&oacute; thể h&ograve;a với sữa hoặc nước để sử dụng.</p>\r\n\r\n<p>Một năm d&ugrave;ng 4-5 đợt , mỗi đợt d&ugrave;ng 4-5 tuần.</p>\r\n\r\n<p><strong>Bảo quản</strong>&nbsp;: Nơi kh&ocirc; r&aacute;o , sạch sẽ, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp, tr&aacute;nh ẩm v&agrave; nơi c&oacute; nhiệt độ cao.</p>\r\n\r\n<p><strong>Quy c&aacute;ch</strong>&nbsp;: Hộp 30 g&oacute;i x 3g/g&oacute;i.</p>\r\n\r\n<p><strong>Gi&aacute; b&aacute;n: 195.000đ.</strong></p>\r\n\r\n<p><strong>SĐK</strong>&nbsp;: 12819/2013/ATTP/XNCB</p>\r\n\r\n<p><strong>Nh&agrave; sản xuất</strong>&nbsp;: C&ocirc;ng ty CP Dược liệu v&agrave; vật tư y tế Hải Ph&ograve;ng.</p>\r\n\r\n<p><strong>Li&ecirc;n hệ tư vấn v&agrave; mua h&agrave;ng:</strong></p>\r\n\r\n<p><strong>Cửa h&agrave;ng EnMax &ndash; 98 Ho&agrave;ng Văn Th&aacute;i &ndash; Thanh Xu&acirc;n &ndash; HN.</strong></p>\r\n\r\n<p><strong>Hotline: 0932 292 136.&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>EnMax giao h&agrave;ng tr&ecirc;n to&agrave;n quốc. Nhận h&agrave;ng &ndash; thanh to&aacute;n tại nh&agrave;.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', '03-26-22-07-06-2016-eu-thymo.jpg', '03-26-22-07-06-2016-eu-thymo.jpg', 'a:1:{i:0;s:32:\"03-26-22-07-06-2016-eu-thymo.jpg\";}', '0', '196', 'Thực phẩm chức năng', '0', '0', '1', '1', '53', 'EnMax 98', '2', '1', null, '22', '1465287982', '1465288056');
INSERT INTO `web_product` VALUES ('598', null, 'Flamasol điều trị viêm khớp dạng thấp', '0', '0', '0', '2', '', '-1', '<p>Flamasol&nbsp;hỗ trợ điều trị tho&aacute;i ho&aacute; khớp v&agrave; vi&ecirc;m khớp cũng như c&aacute;c chứng đau nhức, điều trị c&aacute;c t&igrave;nh trạng vi&ecirc;m m&atilde;n t&iacute;nh do bệnh tự miễn như vi&ecirc;m mạch m&aacute;u tự miễn, lupus đỏ, &hellip; C&aacute;c th&agrave;nh phần của Flamasol c&ograve;n l&agrave;m tăng sức khoẻ cơ bắp, gi&uacute;p hệ tim mạch v&agrave; h&ocirc; hấp hoạt động tốt hơn.</p>\r\n', '<p>Vi&ecirc;m khớp dạng thấp nếu kh&ocirc;ng được điều trị kịp thời, ki&ecirc;n tr&igrave; c&oacute; thể g&acirc;y biến chứng tr&ecirc;n c&aacute;c cơ quan&nbsp;kh&aacute;c của&nbsp;cơ thể như thần kinh ngoại bi&ecirc;n, yếu&nbsp;cơ, bệnh thận, bệnh&nbsp;tim mạch, tổn thương mắt...ảnh hưởng lớn đến sức khỏe cũng như việc sinh hoạt hằng ng&agrave;y bị cản trở.</p>\r\n\r\n<p>Sản phẩm Flamasol với c&aacute;c th&agrave;nh phần thảo dược gi&uacute;p hỗ trợ điều trị vi&ecirc;m khớp dạng thấp, gi&uacute;p việc vận động được tốt hơn, giảm đau, ngăn ngừa được c&aacute;c biến chứng của bệnh g&acirc;y ra.</p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong></p>\r\n\r\n<p>Nhũ Hương, Nh&agrave;u, Bạch thược, Thổ phục linh, Ho&agrave;ng b&aacute;, Sarcandra, L-Carnitine fumarate.</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=598&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/598/03-56-32-07-06-2016-flamasol.jpg\" /></p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:&nbsp;</strong><br />\r\nHỗ trợ điều trị cho người bị vi&ecirc;m đau khớp: thấp khớp, vi&ecirc;m khớp dạng thấp, vi&ecirc;m khớp vẩy nến, tho&aacute;i h&oacute;a khớp.<br />\r\nHỗ trợ điều trị cho người bị c&aacute;c bệnh vi&ecirc;m m&atilde;n t&iacute;nh như bệnh vi&ecirc;m đại tr&agrave;ng m&atilde;n t&iacute;nh, vi&ecirc;m họng m&atilde;n t&iacute;nh, vi&ecirc;m mạch m&aacute;u tự miễn, lu