INSERT INTO `web_product` VALUES ('609', null, 'Sụn vi cá mập - Healthy Care Shark Cartilage 750mg 200 viên', '590000', '750000', '540000', '1', '', '2', '<p><span style=\"color:rgb(29, 33, 41)\">Sụn vi c&aacute; mập - Healthy Care Shark Cartilage 750mg 200 vi&ecirc;n</span><br />\r\n<br />\r\n<span style=\"color:rgb(29, 33, 41)\">Ph&ograve;ng v&agrave; điều trị c&aacute;c bệnh về xương khớp;&nbsp;</span><strong>&nbsp;Sụn vi c&aacute; mập&nbsp;cũng được d&ugrave;ng rất rộng r&atilde;i trong nh&atilde;n khoa; chữa bệnh ung thư;&nbsp;</strong><span style=\"color:rgb(0, 0, 0)\">&nbsp;gi&uacute;p ổn định c&aacute;c bệnh vi&ecirc;m nhiễm da như bệnh vẩy nến v&agrave; eczema</span><br />\r\n<span style=\"color:rgb(29, 33, 41)\">Sản phẩm được c&aacute;c b&aacute;c sỹ khuy&ecirc;n d&ugrave;ng .&nbsp;</span></p>\r\n', '<p><span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">Sụn vi c&aacute; mập - Sụn vi c&aacute; mập &Uacute;c</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">Sụn vi c&aacute; mập&nbsp;</span><br />\r\n<br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">► Quy c&aacute;ch đ&oacute;ng g&oacute;i : 200 vi&ecirc;n/hộp</span><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">► Thương hiệu : Healthy care<br />\r\n► Xuất xứ : &Uacute;c</span><br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=609&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/609/03-51-56-12-06-2016-lnt-sun-vi-ca-mapn540b590.jpg\" /><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">Sụn vi c&aacute; mập - Healthy Care Shark Cartilage 750mg 200 vi&ecirc;n<br />\r\n<br />\r\nSụn vi c&aacute; mập - Ph&ograve;ng v&agrave; điều trị c&aacute;c bệnh về xương khớp .<br />\r\nSản phẩm được c&aacute;c b&aacute;c sỹ khuy&ecirc;n d&ugrave;ng .&nbsp;<br />\r\n<br />\r\nT&aacute;c dụng của sụn vi c&aacute; mập :<br />\r\n- Sụn vi c&aacute; mập gi&uacute;p l&agrave;m giảm đau cho xương v&agrave; cứng khớp ,<br />\r\n<br />\r\n- Sụn vi c&aacute; mập chứa chất chondroitin sulfat, l&agrave; nguy&ecirc;n liệu chủ yếu trong qu&aacute; tr&igrave;nh t&aacute;i tạo m&ocirc; sụn v&agrave; xương. Chất n&agrave;y cũng c&oacute; t&aacute;c dụng ức chế enzym elastase (men n&agrave;y l&agrave; chất trung gian g&acirc;y tho&aacute;i h&oacute;a sụn khớp); K&iacute;ch th&iacute;ch qu&aacute; tr&igrave;nh tổng hợp c&aacute;c proteoglycan (l&agrave; một glycoprotein c&oacute; tỷ lệ glucid rất cao) n&ecirc;n được chỉ định d&ugrave;ng bổ trợ trong c&aacute;c chứng hư v&agrave; tho&aacute;i h&oacute;a khớp<br />\r\n<br />\r\n- Sụn vi c&aacute; mập gi&uacute;p tăng cường hệ miễn dịch, phục hồi v&agrave; duy tr&igrave; cơ, xương v&agrave; c&aacute;c đầu khớp,<br />\r\n- K&iacute;ch th&iacute;ch tế b&agrave;o sụn (Chondrocyte) sản xuất Proteoglycans, Collagen, l&agrave; c&aacute;c chất căn bản cuả sụn khớp.<br />\r\n- Ức chế c&aacute;c men ph&aacute; huỷ sụn, l&agrave;m giảm h&igrave;nh th&agrave;nh c&aacute;c gốc oxy tự do trong m&ocirc; sụn<br />\r\n- Cung cấp dịnh dưỡng cho sụn, gi&uacute;p tạo dịch khớp v&agrave; t&aacute;i tạo sụn khớp.</span></p>\r\n\r\n<p><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=609&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/609/03-53-28-12-06-2016-sun-vi-ca-map-2.jpg\" /><br />\r\n<br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">Sụn vi c&aacute; mập<br />\r\nSản phẩm sụn vi c&aacute; mập được c&aacute;c kh&aacute;ch h&agrave;ng tr&ecirc;n thế giới kiểm chứng<br />\r\n<br />\r\n- Sụn vi c&aacute; mập &Uacute;c gi&uacute;p ổn định c&aacute;c bệnh vi&ecirc;m nhiễm da như bệnh vẩy nến v&agrave; eczema<br />\r\n<br />\r\n- Chữa bệnh ung thư: Sụn vi c&aacute; mập ức chế được hoạt chất angiogenesis - chất c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch qu&aacute; tr&igrave;nh tạo sinh t&acirc;n mạch l&agrave;m cho c&aacute;c khối u, tế b&agrave;o ung thư ph&aacute;t triển.<br />\r\n- Ph&ograve;ng v&agrave; chữa c&aacute;c bệnh về mắt: chondroitin sulfat c&oacute; t&aacute;c dụng tạo độ nhớt th&iacute;ch hợp v&agrave; bồi bổ nội m&ocirc; gi&aacute;c mạc, nu&ocirc;i dưỡng c&aacute;c tế b&agrave;o gi&aacute;c mạc mắt, t&aacute;i tạo lớp ph&iacute;m nước mắt trước gi&aacute;c mạc chống t&igrave;nh trạng kh&ocirc; mắt<br />\r\n<br />\r\n* Sụn vi c&aacute; mập cũng được d&ugrave;ng rất rộng r&atilde;i trong nh&atilde;n khoa.<br />\r\n<br />\r\nNhiều nghi&ecirc;n cứu cho thấy chondroitin sulfat c&oacute; t&aacute;c dụng tạo độ nhớt th&iacute;ch hợp v&agrave; bồi bổ nội m&ocirc; gi&aacute;c mạc, nu&ocirc;i dưỡng c&aacute;c tế b&agrave;o gi&aacute;c mạc mắt, t&aacute;i tạo lớp ph&iacute;m nước mắt trước gi&aacute;c mạc chống t&igrave;nh trạng kh&ocirc; mắt.<br />\r\n<br />\r\nChondroitin sulfat cũng c&oacute; t&aacute;c dụng duy tr&igrave; độ trong suốt của thủy tinh thể v&agrave; gi&aacute;c mạc mắt, tăng cường t&iacute;nh đ&agrave;n hồi của thấu k&iacute;nh v&agrave; thể mi khi mắt điều tiết n&ecirc;n hạn chế sự kh&ocirc; mắt, mỏi mắt, hoa mắt khi mắt l&agrave;m việc qu&aacute; nhiều. Ngo&agrave;i ra, chondroitin sulfat c&ograve;n được d&ugrave;ng điều trị hỗ trợ trong phẫu thuật mắt (mổ cườm, đục thủy tinh thể) v&agrave; d&ugrave;ng l&agrave;m dung dịch bảo quản gi&aacute;c mạc trong phẫu thuật gh&eacute;p gi&aacute;c mạc.<br />\r\n<br />\r\nDo ức chế tạo sinh t&acirc;n mạch, chondroitin sulfat c&ograve;n được d&ugrave;ng trong điều trị bệnh tho&aacute;i h&oacute;a v&otilde;ng mạc mắt do tiểu đường (bệnh do t&acirc;n mạch ph&aacute;t triển x&acirc;m nhập v&agrave;o dưới v&otilde;ng mạc mắt g&acirc;y tổn thương th&agrave;nh sẹo, c&oacute; thể dẫn tới m&ugrave; l&ograve;a).<br />\r\n<br />\r\n<strong>Ch&uacute; &yacute;:</strong><br />\r\n<br />\r\n- Hiện nay c&aacute;c thuốc chứa Sụn vi c&aacute; mập (chondroitin sulfat) trong th&agrave;nh phần của thuốc như bitalothin, hirmen, neuralgin, prochon, tobicom... đang c&oacute; b&aacute;n rộng r&atilde;i ở c&aacute;c cửa h&agrave;ng dược phẩm nước ta.<br />\r\nTuy nhi&ecirc;n, do đặc t&iacute;nh của chất chiết suất từ Sụn vi c&aacute; mập ức chế tạo sinh t&acirc;n mạch, cho n&ecirc;n những người đang cần tạo t&acirc;n mạch như: trẻ em, phụ nữ muốn c&oacute; thai hoặc đang mang thai, đang thời kỳ nu&ocirc;i con b&uacute;, người bị tai biến tim mạch, mới trải qua phẫu thuật lớn, mới bị bỏng diện rộng, vận động vi&ecirc;n cần ph&aacute;t triển cơ bắp (tập thể h&igrave;nh, cử tạ...) th&igrave; kh&ocirc;ng n&ecirc;n d&ugrave;ng chondroitin sulfat.<br />\r\n<br />\r\nTh&agrave;nh phần của sụn vi c&aacute; mập :<br />\r\n<br />\r\n- Sụn vi c&aacute; mập 750mg<br />\r\n<br />\r\nC&aacute;ch d&ugrave;ng:&nbsp;<br />\r\n<br />\r\n- Ng&agrave;y uống 2 hoặc 3 vi&ecirc;n hoặc t&ugrave;y theo sự chỉ dẫn của b&aacute;c sĩ</span></p>\r\n', '03-51-56-12-06-2016-lnt-sun-vi-ca-mapn540b590.jpg', '03-53-28-12-06-2016-sun-vi-ca-map-2.jpg', 'a:2:{i:0;s:49:\"03-51-56-12-06-2016-lnt-sun-vi-ca-mapn540b590.jpg\";i:1;s:39:\"03-53-28-12-06-2016-sun-vi-ca-map-2.jpg\";}', '0', '201', 'Chăm sóc sức khỏe', '0', '0', '1', '1', '55', 'Siêu thị gia đình', '3', '1', null, '22', '1465721515', '1470622518');
INSERT INTO `web_product` VALUES ('610', null, 'Cách phòng dịch sốt xuất huyết', '90000', '0', '0', '1', '', '-1', '<p>Bệnh sốt xuất huyết c&oacute; thể g&acirc;y&nbsp;nguy hiểm đến t&iacute;nh mạng người bệnh&nbsp;v&igrave; vậy ch&uacute;ng ta kh&ocirc;ng n&ecirc;n chủ quan trong việc ph&ograve;ng v&agrave; chữa bệnh. Để ngăn chặn dịch bệnh sốt xuất huyết&nbsp;ch&uacute;ng ta cần phải c&oacute; những biện ph&aacute;p ph&ograve;ng bệnh một c&aacute;ch chủ động. Li&ecirc;n hệ tư vấn: 0932 292 136</p>\r\n', '<p><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\">Sốt xuất huyết đang b&ugrave;ng ph&aacute;t th&agrave;nh dịch, đ&acirc;y l&agrave; loại bệnh dịch nguy hiểm, trong khi đ&oacute; rất nhiều gia đ&igrave;nh c&ograve;n chủ quan trong việc ph&ograve;ng bệnh v&agrave; điều trị bệnh, n&ecirc;n dẫn đến tỉ lệ ngưởi tử vong do sốt xuất huyết ng&agrave;y c&agrave;ng gia tăng.<br />\r\nCh&uacute;ng ta cần nghi&ecirc;m t&uacute;c hơn trong việc ph&ograve;ng bệnh v&agrave; điều trị bệnh.<br />\r\nĐể điều trị bệnh c&oacute; hiệu quả cần phải biết được nguy&ecirc;n nh&acirc;n v&agrave; triệu chứng của bệnh.<br />\r\nBệnh sốt xuất huyết thường c&oacute; 2 <strong>NGUY&Ecirc;N NH&Acirc;N</strong> chủ yếu g&acirc;y ra bệnh:<br />\r\n- Do si&ecirc;u vi tr&ugrave;ng Dengue g&acirc;y ra.<br />\r\n- Do muỗi vằn h&uacute;t m&aacute;u người mắc bệnh mang đến cho người l&agrave;nh. Đ&acirc;y l&agrave; nguy&ecirc;n nh&acirc;n phổ biến v&agrave; dễ tạo th&agrave;nh dịch nhất.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\"><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=610&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/610/11-09-37-13-06-2016-sot-xuat-huyet.jpg\" /></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\"><strong>L&Agra