INSERT INTO `web_product` VALUES ('622', null, 'Kem đánh giày cao cấp GoldCare - GC 1001', '59000', '0', '0', '1', '', '-1', '<p>Gi&uacute;p da gi&agrave;y mềm mại, b&oacute;ng lo&aacute;ng ngay tức th&igrave; m&agrave; kh&ocirc;ng mất thời gian đ&aacute;nh b&oacute;ng. L&agrave;m mờ v&agrave; giảm c&aacute;c vết trầy xước tr&ecirc;n da gi&agrave;y, bảo vệ da gi&agrave;y chống bong tr&oacute;c, gi&uacute;p da gi&agrave;y kh&ocirc;ng thấm nước.<br />\r\nD&ugrave;ng cho gi&agrave;y da v&agrave; c&aacute;c phụ kiện da.</p>\r\n', '<p>- Kem đ&aacute;nh gi&agrave;y Gold Care - GC 1001 l&agrave; sản phẩm c&oacute; xuất xứ từ Thổ Nhĩ Kỳ, được nhập khẩu v&agrave; ph&acirc;n phối độc quyền bởi C&ocirc;ng ty Cổ phần Gilos.<br />\r\n- Sản phẩm dạng kem, chuy&ecirc;n d&ugrave;ng đ&aacute;nh b&oacute;ng gi&agrave;y da v&agrave; c&aacute;c phụ kiện bằng da như quần &aacute;o, t&uacute;i x&aacute;ch, sofa da&hellip;<br />\r\n- Ngo&agrave;i t&aacute;c dụng l&agrave;m sạch, l&agrave;m b&oacute;ng, l&agrave;m mới, Gold Care - GC 1001 c&ograve;n c&oacute; t&aacute;c dụng chống bong tr&oacute;c, chống thấm nước, bảo vệ bề mặt da, l&agrave;m tăng độ bền cho đ&ocirc;i gi&agrave;y.<br />\r\n- C&oacute; khả năng thẩm thấu cực nhanh v&agrave;o bề mặt da, kh&aacute;c biệt ho&agrave;n to&agrave;n so với những loại xi th&ocirc;ng thường.</p>\r\n\r\n<p>- Sản phẩm k&egrave;m theo đầu m&uacute;t tiện cho việc đ&aacute;nh b&oacute;ng, kh&ocirc;ng cần phải sử dụng b&agrave;n chải, tiết kiệm v&agrave; tiện lợi hơn.<br />\r\n- Kiểu d&aacute;ng nhỏ gọn, dễ d&agrave;ng mang theo khi di du lịch, c&ocirc;ng t&aacute;c.<br />\r\n- C&oacute; 3m&agrave;u: đen, n&acirc;u, n&acirc;u đen.</p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>Th&ocirc;ng tin li&ecirc;n hệ:<br />\r\nC&ocirc;ng ty Cổ Phần Gilos<br />\r\nĐịa chỉ: Tầng 4, T&ograve;a nh&agrave; Thanh Ni&ecirc;n, 345/134 Trần Hưng Đạo, P.Cầu Kho, Quận 1, TP.HCM<br />\r\nĐiện thoại: (08) 22 242 168 - 0908 340 057<br />\r\nEmail: trieubui@gilos.com.vn<br