INSERT INTO `web_product` VALUES ('671', null, '[HEALTHY CARE VITAMIN E 500IU] - 200VIÊN', '610000', '680000', '580000', '1', '', '1', '<p><span style=\"color:rgb(29, 33, 41)\">Vitamin E l&agrave; một chất chống oxy h&oacute;a dinh dưỡng quan trọng, gi&uacute;p bảo vệ tế b&agrave;o khỏi gốc tự do,duy tr&igrave; cholesterol, duy tr&igrave; sức khỏe tim mạch,hỗ trợ sức khỏe của mắt.</span></p>\r\n', '<p><span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">Trong thực phẩm, c&aacute;c nguồn phổ biến nhất chứa vitamin E l&agrave; c&aacute;c loại dầu thực vật như cọ dầu, hướng dương, ng&ocirc;, đậu tương, &ocirc; liu. C&aacute;c loại quả ki&ecirc;n, hạt hướng dương, quả nh&oacute;t gai (Hippophae spp.), dương đ&agrave;o (Actinidia spp.) v&agrave; mầm l&uacute;a m&igrave; cũng l&agrave; c&aacute;c nguồn cung cấp vitamin E. C&aacute;c nguồn kh&aacute;c c&oacute; hạt ngũ cốc, c&aacute;, bơ lạc, c&aacute;c loại rau&nbsp;l&aacute; xanh. Mặc d&ugrave; ban đầu vitamin E được chiết ra từ dầu mầm l&uacute;a m&igrave;, nhưng phần lớn c&aacute;c nguồn bổ sung vitamin E tự nhi&ecirc;n hiện nay lại t&aacute;ch ra từ dầu thực vật, th&ocirc;ng thường l&agrave; dầu đậu tương.</span><br />\r\n<img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=671&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/671/11-22-15-08-07-2016-lnt-healthy-caren580.jpg\" /><br />\r\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">H&agrave;m lượng vitamin E của một số nguồn như sau:<br />\r\nDầu mầm l&uacute;a m&igrave; (215,4 mg/100 g)<br />\r\nDầu hướng dương (55,8 mg/100 g)<br />\r\nQuả phỉ (26.0 mg/100 g)<br />\r\nDầu &oacute;c ch&oacute; (20,0 mg/100 g)<br />\r\nDầu lạc (17,2 mg/100 g)<br />\r\nDầu &ocirc; liu (12,0 mg/100 g)<br />\r\nLạc (9,0 mg/100 g)<br />\r\nC&aacute;m mịn (2,4 mg/100 g)<br />\r\nNg&ocirc; (2,0 mg/100 g)<br />\r\nMăng t&acirc;y (1,5 mg/100 g)<br />\r\nYến mạch (1,5 mg/100 g)<br />\r\nDẻ (1,2 mg/100 g)<br />\r\nDừa (1,0 mg/100 g)<br />\r\nC&agrave; chua (0,9 mg/100 g)<br />\r\nC&agrave; rốt (0,6 mg/100 g)<br />\r\nC&aacute;c rối loạn li&ecirc;n quan[sửa | sửa m&atilde; nguồn]<br />\r\nThiếu vitamin E<br />\r\nC&oacute; thể gặp ở trẻ đẻ non, người lớn bị cắt t&uacute;i mật. Khi thiếu Vitamin E k&eacute;o d&agrave;i sẽ c&oacute; c&aacute;c triệu chứng thần kinh như: thất điều, yếu cơ, rung giật nh&atilde;n cầu, x&uacute;c gi&aacute;c giảm nhạy cảm.<br />\r\n<br />\r\nThừa Vitamin E<br />\r\nNếu d&ugrave;ng Vitamin liều cao (tr&ecirc;n 3000 IU mỗi ng&agrave;y) c&oacute; thể g&acirc;y rối loạn ti&ecirc;u h&oacute;a (buồn n&ocirc;n, đầy hơi, đi lỏng, vi&ecirc;m ruột hoại tử). Ti&ecirc;m tĩnh mạch liều cao c&oacute; thể g&acirc;y tử vong.<br />\r\n<br />\r\nChỉ định d&ugrave;ng<br />\r\nTrong sản khoa: đe doạ sẩy thai<br />\r\nĐ&aacute;i dầm sau đẻ hoặc ở phụ nữ m&atilde;n kinh<br />\r\nNam giới v&ocirc; sinh, thiểu năng tinh tr&ugrave;ng<br />\r\nCận thị tiến triển<br />\r\nC&aacute;c chỉ định kh&aacute;c: teo cơ do thần kinh, rối loạn kinh nguyệt thời kỳ m&atilde;n kinh, bệnh cứng b&igrave; ở trẻ em, loạn dưỡng, hấp thu k&eacute;m thức ăn, tắc đường mật.<br />\r\n<br />\r\nTh&agrave;nh phần: Mỗi vi&ecirc;n chứa<br />\r\ndl-alpha Tocopherol 460mg (500IU)<br />\r\n<br />\r\nC&aacute;ch sử dụng:<br />\r\nUống 1 vi&ecirc;n/lần, 1-2 lần mỗi ng&agrave;y</span></p>\r\n', '11-22-15-08-07-2016-lnt-healthy-caren580.jpg', '11-22-15-08-07-2016-lnt-healthy-caren580.jpg', 'a:1:{i:0;s:44:\"11-22-15-08-07-2016-lnt-healthy-caren580.jpg\";}', '0', '178', 'Y tế cho mẹ & bé', '0', '0', '1', '1', '55', 'Siêu thị gia đình', '3', '1', null, '22', '1467951735', '1467952063');
