INSERT INTO `web_product` VALUES ('678', null, 'BALO TOMI B15', '449000', '499000', '499000', '1', '', '2', '<p>Gồm một ngăn ch&iacute;nh lớn chứa đầy đủ s&aacute;ch vở, trong c&ograve;n c&oacute; 2 ngăn nhỏ đựng giấy vẽ b&agrave;i kiểm tra.</p>\r\n\r\n<p>Hộp mặt trước kh&aacute; rộng với h&igrave;nh ảnh dập nổi lớn được in kh&aacute; đẹp v&agrave; sắc sảo của những c&ocirc;ng ch&uacute;a hay si&ecirc;u nh&acirc;n trong phim thần thoại &ndash; cổ t&iacute;ch. Hộp c&oacute; thể chứa cả một chiếc &aacute;o kho&aacute;c nhẹ, một hộp b&uacute;t v&agrave; dụng cụ học tập. Mở ra b&ecirc;n trong c&ograve;n c&oacute; một ngăn lưới phụ chứa vật dụng linh tinh kh&aacute;c.</p>\r\n\r\n<p>Hai ngăn lưới b&ecirc;n h&ocirc;ng lớn, chứa thoải m&aacute;i nước, sữa, đồ hộp kh&aacute;c.</p>\r\n\r\n<p>Cần k&eacute;o thanh nh&ocirc;m đơn, trọng lượng nhẹ, &acirc;m hẳn ph&iacute;a sau bal&ocirc;, c&oacute; thể th&aacute;o lắp dễ d&agrave;ng do Cần k&eacute;o Năm sao bảo chứng. Bảo đảm sử dụng cho 2 năm li&ecirc;n tục v&agrave; sửa chữa thay thế kh&ocirc;ng thời hạn.</p>\r\n\r\n<p>Đệm lưng bằng lưới tho&aacute;ng kh&iacute; gi&uacute;p tho&aacute;t mồ h&ocirc;i v&agrave; giảm nhiệt tiếp x&uacute;c lưng trẻ.</p>\r\n\r\n<p>Kiểu d&aacute;ng hiện đại, đơn giản, năng động. D&agrave;nh cho học sinh tiểu học từ lớp 1 đến lớp 4</p>\r\n\r\n<p>K&iacute;ch thước 42 x 32 x 18cm; cần k&eacute;o đơn 3 đoạn k&eacute;o d&agrave;i 78cm</p>\r\n\r\n<p><strong>Trọng lượng bal&ocirc; l&agrave; 0.6kg, trọng lượng cần k&eacute;o 0.38kg</strong></p>\r\n', '', '09-59-08-09-07-2016-tomi-b15-xanh-dam-3.jpg', '09-59-29-09-07-2016-tomi-b15-xanh-lot-1.jpg', 'a:2:{i:0;s:43:\"09-59-08-09-07-2016-tomi-b15-xanh-dam-3.jpg\";i:1;s:43:\"09-59-29-09-07-2016-tomi-b15-xanh-lot-1.jpg\";}', '0', '170', 'Bé học và chơi', '0', '0', '1', '1', '65', 'TOMIMACAT', '1', '1', null, '29', '1468033148', '1470622329');
INSERT INTO `web_product` VALUES ('679', null, 'CẶP TOMI FUTURE 6', '529000', '565000', '565000', '1', '', '2', '<p>T&iacute;nh năng cặp TOMI FUTURE 6</p>\r\n\r\n<p>&ndash; Gồm một ngăn ch&iacute;nh lớn chứa đầy đủ s&aacute;ch vở, trong c&ograve;n chia th&ecirc;m hai ngăn nhỏ.</p>\r\n\r\n<p>&ndash; Hộp mặt trước kh&aacute; rộng với khung h&igrave;nh ảnh để thay thế, che phủ v&agrave; bảo vệ bởi m&agrave;ng nhựa trong rất bền.</p>\r\n\r\n<p>&ndash; C&oacute; thể chứa hộp b&uacute;t lớn v&agrave; dụng cụ học tập. Hai ngăn lưới b&ecirc;n h&ocirc;ng lớn, chứa thoải m&aacute;i nước, sữa, đồ hộp kh&aacute;c.</p>\r\n\r\n<p>&ndash; Khi đem nhiều s&aacute;ch vở th&igrave; c&oacute; thể lắp th&ecirc;m cần k&eacute;o hoặc khi đi du lịch th&igrave; gắn th&ecirc;m b&aacute;nh xoay &acirc;m hẳn ph&iacute;a sau bal&ocirc;, do Cần k&eacute;o Năm sao bảo chứng. Bảo đảm sử dụng cho 2 năm li&ecirc;n tục v&agrave; sửa chữa thay thế kh&ocirc;ng thời hạn.</p>\r\n\r\n<p>&ndash; Đệm lưng bằng lưới tho&aacute;ng kh&iacute; gi&uacute;p tho&aacute;t mồ h&ocirc;i v&agrave; giảm nhiệt tiếp x&uacute;c lưng trẻ.</p>\r\n\r\n<p>&ndash; D&agrave;nh cho học sinh tiểu học từ lớp 3 đến lớp 5</p>\r\n\r\n<p>&ndash; K&iacute;ch thước cặp ti&ecirc;u chuẩn 36 x 30 &ndash; 32 x 18cm; cần k&eacute;o đơn 3 đoạn k&eacute;o d&agrave;i 78cm</p>\r\n\r\n<p><strong>&ndash; Trọng lượng bal&ocirc; l&agrave; 0.86kg, trọng lượng cần k&eacute;o nếu gắn th&ecirc;m 0.38kg</strong></p>\r\n\r\n<p><strong>&ndash; H&igrave;nh thay thế ti&ecirc;u chuẩn (&eacute;p plastic) 15x21cm, nếu khổ lớn 20x25cm th&igrave; cắt theo khung c&oacute; sẵn.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '10-04-52-09-07-2016-f6-blue-1.jpg', '10-12-57-09-07-2016-f6-pink-2.jpg', 'a:4:{i:0;s:33:\"10-04-52-09-07-2016-f6-blue-1.jpg\";i:1;s:33:\"10-12-57-09-07-2016-f6-pink-2.jpg\";i:2;s:38:\"10-13-14-09-07-2016-f6-blue-dark-5.jpg\