INSERT INTO `web_product` VALUES ('691', null, 'BÌNH SỮA NHÍ PAPPI 60ML', '39000', '0', '0', '1', '', '1', '', '<p><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=691&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/691/03-45-31-31-07-2016-binh-nhφ-2.jpg\" /><img src=\"http://shopcuatui.com.vn/image.php?type_dir=product&amp;id=691&amp;width=700&amp;height=700&amp;image=http://shopcuatui.com.vn/uploads/product/691/03-46-38-31-07-2016-binh-nhφ-1.jpg\" /><span style=\"background-color:rgb(255, 255, 255); color:rgb(80, 80, 80); font-family:times new roman,times,serif; font-size:18.6667px\">B&igrave;nh Sữa Nh&iacute; Pappi 60ml, ngo&agrave;i việc đựng sữa d&agrave;nh cho b&eacute; c&oacute; thể d&ugrave;ng để đựng nước v&agrave; thực phẩm dạng lỏng kh&aacute;c.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-family:times new roman,times,serif; font-size:14pt\">Chất liệu an to&agrave;n</span></strong></p>\r\n\r\n<p><span style=\"font-family:times new roman,times,serif; font-size:14pt\">Được l&agrave;m từ nhựa cao cấp, đạt chuẩn an to&agrave;n, kh&ocirc;ng chứa BPA&nbsp;(Bisphenol A), kh&ocirc;ng chứa chất động hại.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:times new roman,times,serif; font-size:14pt\">Được l&agrave;m bằng chất liệu &nbsp;trong mềm mại,&nbsp;cho b&eacute; cảm gi&aacute;c như đang ti mẹ v&agrave; c&oacute; khả năng chống tưa lưỡi cho b&eacute;.</span></p>\r\n\r\n<p><span style=\"font-family:times new roman,times,serif; font-size:14pt\">Đi k&egrave;m&nbsp;<a href=\"http://sieuthiaau.com/\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; text-decoration: none; cursor: pointer; transition: all 0.2s ease-in-out 0s; color: rgb(32, 32, 32); background: transparent;\">b&igrave;nh sữa</a>&nbsp;l&agrave; van chống sặc,&nbsp;c&oacute; t&aacute;c dụng l&agrave;m giảm bọt kh&iacute; khi b&uacute; b&igrave;nh. Việc nuốt nhiều bọt kh&iacute; đượ